
let btn = document.querySelector('.calk')
btn.addEventListener('click', function () {
	let dlina = document.querySelector('#dlina').value
	let shirina = document.querySelector('#shirina').value
	let visota = document.querySelector('#visota').value
	let select = document.querySelector('#select')
	let count = document.querySelector('#count').value
	let days = document.querySelector('#days').value

	let info = document.querySelector('.calculator__info')
	let cifra = document.querySelector('.num')
	let fixPrice = 540


	if (select.value == 'sm') {
		dlina = dlina / 100
		shirina = shirina / 100
		visota = visota / 100
	}

	let tsena = dlina * shirina * visota * count * days * fixPrice
	console.log(tsena)
	cifra.innerHTML = Math.ceil((tsena) * 100) / 100
	document.querySelector('.kub').innerHTML = Math.ceil((dlina * shirina * visota) * 100) / 100
	info.classList.add('_active')

})